import { Dict } from "@/app/i18n/get-dictionary";
import { Container } from "../ui/Container";
import { Section } from "../ui/Section";
import { FeatureCard } from "../ui/FeatureCard";
import { Fragment } from "react/jsx-runtime";
import { ParallaxElement } from "../ui/ParallaxElement";
import { SectionTitle } from "../ui/SectionTitle";

interface AdvantagesProps {
	dict: Dict["advantages"];
}

const icons = [
	"/images/icons/calendar.png",
	"/images/icons/globe.png",
	"/images/icons/gift.png",
	"/images/icons/chart.png",
	"/images/icons/slots.png",
	"/images/icons/stats.png",
	"/images/icons/card.png",
];

export function Advantages({ dict }: AdvantagesProps) {
	return (
		<Section
			id="advantages"
			bgImage="/images/advantages/advantages-background.png"
			bgClassName="top-[20%] h-[120%] w-full"
			className="relative z-10 min-h-screen flex items-center pt-12 sm:pt-16 md:pt-20 lg:pt-0"
		>
			<div className="absolute bottom-0 left-0 w-full h-48 sm:h-60 lg:h-75 bg-linear-to-t from-text-inverse via-text-inverse/80 to-transparent z-0 pointer-events-none" />

			<Container>
				<ParallaxElement speed={2} type="fly-up">
					<SectionTitle title={dict.title} className="mb-10 sm:mb-16 md:mb-20 lg:mb-26" />
				</ParallaxElement>

				<ParallaxElement speed={1.5}>
					<div className="w-full h-max flex flex-wrap justify-center sm:gap-x-4 lg:gap-x-6 mb-8 sm:mb-12 lg:mb-18">
						{dict.items.map((item, index) => {
							const isEndOfRowMD = (index + 1) % 2 === 0;
							const isEndOfRowLG = index === 3 || index === 6;
							const isTopRowLG = index < 4;

							let lineClasses =
								"absolute w-[1px] right-[-8px] lg:right-[-12px] hidden ";

							if (!isEndOfRowMD) {
								lineClasses +=
									"md:block md:top-[10%] md:bottom-[10%] md:bg-gradient-to-b md:from-transparent md:via-white/20 md:to-transparent ";
							} else {
								lineClasses += "md:hidden ";
							}

							if (isEndOfRowLG) {
								lineClasses += "lg:hidden ";
							} else {
								lineClasses += "lg:block ";

								if (isTopRowLG) {
									lineClasses +=
										"lg:top-[10%] lg:bottom-0 lg:bg-gradient-to-b lg:from-transparent lg:to-white/20 ";
								} else {
									lineClasses +=
										"lg:top-0 lg:bottom-[10%] lg:bg-gradient-to-b lg:from-white/20 lg:to-transparent ";
								}
							}

							return (
								<Fragment key={index}>
									<div className="relative w-full xs:w-[80%] sm:w-[48%] md:w-[46%] lg:w-[23%] flex justify-center py-4 sm:py-6 lg:py-8">
										<FeatureCard
											index={index}
											icon={icons[index]}
											title={item.title}
											description={item.desc}
										/>

										<div className={lineClasses} />
									</div>

									{index === 3 && (
										<div className="hidden lg:flex w-full h-auto items-center justify-center relative z-0">
											<div className="w-full h-0.5 bg-linear-to-r from-transparent via-white/20 to-transparent" />
										</div>
									)}

									{isEndOfRowMD && index < dict.items.length - 1 && (
										<div className="hidden md:flex lg:hidden w-full h-auto items-center justify-center relative z-0 my-2">
											<div className="w-[80%] h-0.5 bg-linear-to-r from-transparent via-white/20 to-transparent" />
										</div>
									)}
								</Fragment>
							);
						})}
					</div>
				</ParallaxElement>
			</Container>
		</Section>
	);
}

import { Dict } from "@/app/i18n/get-dictionary";
import { Container } from "../ui/Container";
import { Section } from "../ui/Section";
import { FeatureCard } from "../ui/FeatureCard";
import { Fragment } from "react/jsx-runtime";
import { ParallaxElement } from "../ui/ParallaxElement";
import { SectionTitle } from "../ui/SectionTitle";

interface AdvantagesProps {
	dict: Dict["advantages"];
}

const icons = [
	"/images/icons/calendar.png",
	"/images/icons/globe.png",
	"/images/icons/gift.png",
	"/images/icons/chart.png",
	"/images/icons/slots.png",
	"/images/icons/stats.png",
	"/images/icons/card.png",
];

export function Advantages({ dict }: AdvantagesProps) {
	return (
		<Section
			id="advantages"
			bgImage="/images/advantages/advantages-background.png"
			bgClassName="top-[20%] h-[120%] w-full"
			className="relative z-10 min-h-screen flex items-center pt-12 sm:pt-16 md:pt-20 lg:pt-0"
		>
			<div className="absolute bottom-0 left-0 w-full h-48 sm:h-60 lg:h-75 bg-linear-to-t from-text-inverse via-text-inverse/80 to-transparent z-0 pointer-events-none" />

			<Container>
				<ParallaxElement speed={2} type="fly-up">
					<SectionTitle title={dict.title} className="mb-10 sm:mb-16 md:mb-20 lg:mb-26" />
				</ParallaxElement>

				<ParallaxElement speed={1.5}>
					<div className="w-full h-max flex flex-wrap justify-center sm:gap-x-4 lg:gap-x-6 mb-8 sm:mb-12 lg:mb-18">
						{dict.items.map((item, index) => {
							const isEndOfRowMD = (index + 1) % 2 === 0;
							const isLastItem = index === dict.items.length - 1;
							const isEndOfRowLG = index === 3 || index === 6;
							const isTopRowLG = index < 4;

							let lineClasses =
								"absolute w-[1px] right-[-8px] lg:right-[-12px] hidden ";

							if (!isEndOfRowMD && !isLastItem) {
								lineClasses +=
									"md:block md:top-0 md:bottom-0 md:bg-white/20 ";
							} else {
								lineClasses += "md:hidden ";
							}

							if (isEndOfRowLG) {
								lineClasses += "lg:hidden ";
							} else {
								lineClasses += "lg:block ";

								if (isTopRowLG) {
									lineClasses +=
										"lg:top-[10%] lg:bottom-0 lg:bg-gradient-to-b lg:from-transparent lg:to-white/20 ";
								} else {
									lineClasses +=
										"lg:top-0 lg:bottom-[10%] lg:bg-gradient-to-b lg:from-white/20 lg:to-transparent ";
								}
							}

							return (
								<Fragment key={index}>
									<div className="relative w-full xs:w-[80%] sm:w-[48%] md:w-[46%] lg:w-[23%] flex justify-center py-4 sm:py-6 lg:py-8">
										<FeatureCard
											index={index}
											icon={icons[index]}
											title={item.title}
											description={item.desc}
										/>

										<div className={lineClasses} />
									</div>

									{index === 3 && (
										<div className="hidden lg:flex w-full h-auto items-center justify-center relative z-0">
											{/* Горизонтальная линия ровно посередине высоты h-12 */}
											<div className="w-full h-0.5 bg-linear-to-r from-transparent via-white/20 to-transparent" />
										</div>
									)}

									{isEndOfRowMD && index < dict.items.length - 1 && (
										<div className="hidden md:flex lg:hidden w-full h-auto items-center justify-center relative z-0">
											<div className="w-[80%] h-0.5 bg-linear-to-r from-transparent via-white/20 to-transparent" />
										</div>
									)}
								</Fragment>
							);
						})}
					</div>
				</ParallaxElement>
			</Container>
		</Section>
	);
}

"use client";

import { motion, useScroll, useTransform } from "framer-motion";
import Image from "next/image";
import { ReactNode, useContext, useRef } from "react";
import { ScrollContext } from "./ScrollContext";

interface SectionProps {
	children: ReactNode;
	id?: string;
	className?: string;
	bgImage?: string;
	bgClassName?: string;
	parallaxRange?: [string, string];
	overlayOpacity?: number;
}

export function Section({
	children,
	id,
	className = "",
	bgImage,
	bgClassName,
	parallaxRange = ["-10%", "10%"],
	overlayOpacity = 0,
}: SectionProps) {
	const sectionRef = useRef<HTMLElement>(null);
	const containerRef = useContext(ScrollContext);

	const { scrollYProgress } = useScroll({
		target: sectionRef,
		container: containerRef || undefined,
		offset: ["start end", "end start"],
	});

	const y = useTransform(scrollYProgress, [0, 1], parallaxRange);

	const defaultBgClass = "absolute -top-[15%] h-[130%] w-full";
	const finalBgClass = bgClassName || defaultBgClass;

	return (
		<section
			id={id}
			ref={sectionRef}
			className={`
                relative w-full h-screen min-h-200 flex items-center
                snap-start snap-always overflow-hidden
                ${className}
            `}
		>
			{bgImage && (
				<motion.div
					style={{ y }}
					className={`absolute inset-0 -z-20 ${finalBgClass}`}
				>
					<Image
						src={bgImage}
						alt="Section background"
						fill
						className="object-cover object-top"
						priority={id === "hero"}
					/>

					{overlayOpacity > 0 && (
						<div
							className="absolute inset-0 bg-black pointer-events-none"
							style={{ opacity: overlayOpacity }}
						/>
					)}
				</motion.div>
			)
			}

			<div className="relative z-10 w-full h-full flex items-center justify-center pt-(--header-height)">
				{children}
			</div>
		</section >
	);
}

